import styles from "./Control.module.scss";

export const Control = props => {

    const { name, inputType, onChange, labelText } = props;

    let input = null;
    if (inputType === 'input') {
        input = <input name={name} onChange={onChange} />
    } else {
        input = <textarea name={name} onChange={onChange}/>
    }

    return (
        <div className={styles.control}>
            {input}
          <label>{labelText}</label>
        </div>
    )
}
