import React from "react";
import styles from './SupportForm.module.scss';

export class SupportForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            userName: '',
            comment: ''
        }
    }

    handleInputChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        this.setState({
            [name]: value
        });
    }

    handleSubmit = (event) => {
        event.preventDefault();
        alert('UserName is: ' + this.state.userName + ', comment: ' + this.state.comment);
    }

    render() {
        return (
            <div>
                <h2>Please wright your request</h2>
                <form onSubmit={this.handleSubmit}>
                    <div>
                        <input type="text" name="username" onChange={this.handleInputChange} required/>
                        <label>Username</label>
                    </div>
                    <div>
                        <textarea name="comment" onChange={this.handleInputChange} required/>
                        <label>Comment</label>
                    </div>
                    <button>
                        <span></span>
                        <span></span>
                        <span></span>
                        <span></span>
                        Submit
                    </button>
                </form>
            </div>
        );
    }
}
